mapper.select:
SELECT USER_ID, USER_NAME, USER_SEQ, USER_PW
FROM april.tb_user_mst
WHERE USER_ID = #{userId}

mapper.insert:

    SELECT COALESCE(MAX(USER_SEQ), 0) + 1 FROM tb_user_mst

INSERT INTO tb_user_mst
(
USER_SEQ
, USER_ID
, USER_PW
, USER_NAME
, CRE_ID
, CRE_DT
, UDT_ID
, UDT_DT
)
VALUES
(
#{userSeq}
,#{userId}
,#{userPw}
,#{userName}
,#{userId}
, date_format(now(),'%Y%m%d%H%i%s')
,#{userId}
, date_format(now(),'%Y%m%d%H%i%s')
)
